import styled from 'styled-components'


export const CardStyle = styled.div`

    .container {
        padding-top: 60px;
        display: flex;
        flex-direction: column;
        background: purple;
        margin-left: 200px;
        height: 100vh;
    }

    .search {
        display: flex;
            & svg {
                font-size: 45px;
                margin-left: 50px;
                cursor: pointer;
                color: #00FF7F;
            }     
    }


    input {
        margin-left 30%;
        margin-top: 0px;
        margin-bottom: 75px;

        width: 350px;
        height: 40px;
        padding-left: 10px;
    }


    .cards {
        background: red;
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;
        height: 100vh;

    }

    .card {
        background: #1E212A;
        width: 365px;
        height: 190px;
        display: flex;
        border-radius: 10px; 
        justify-content: space-between;
        cursor: pointer;
    }


    .image {
        width: 115px;
        height: 115px;
        background: black;
        margin-top: 12px;
        margin-left: 12px;
        border-radius: 10px;
    }

    .infos {
        margin-right: 115px;
        display: flex;
        flex-direction: column;
        justify-content: space-evenly;
    }

    p {
        margin: 0;
        color: #ffff;
        font-weight: 450;
    }


`
