import React, { useState } from "react";
import { ModalStyle } from "./styles";
import CloseIcon from '@mui/icons-material/Close';
import { apiMock } from "../../Api";
import MuiAlert, { AlertProps } from '@mui/material/Alert';
import { Snackbar } from "@mui/material";


interface Jogador {
    nomeJogador: string;
    idadeJogador: number;
    nacionalidadeJogador: string;
    clubeJogador: string;
    posicaoJogador: string;
    overallJogador: number;
    valorJogador: number;
    fotoJogador: string;
    idUsuario: number;
    id: number;
}

interface Usuario {
    id: number,
    username: string,
    email: string,
    password: string
    wallet: number
}


const Alert = React.forwardRef<HTMLDivElement, AlertProps>(function Alert(
    props,
    ref,
) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});


const ModalConfirmacao = ({ onClose = () => { }, children, idJogador }) => {
    const [open, setOpen] = React.useState(false);

    let usuario = localStorage.getItem('usuario');
    const usuarioData: Usuario = JSON.parse(usuario)


    async function PatchIdUsuario() {
        try {
            const res = await apiMock.patch<Jogador[], any>(`/jogadores/${idJogador}`, {

                idUsuario: usuarioData[0].id

            });

            onClose();

        } catch (err) {
            console.log(err)
        }
    }


    const handleClick = () => {
        setOpen(true);
        PatchIdUsuario();
    };


    const handleClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };


    return (

        <ModalStyle>

            <div className="modal">
                <CloseIcon onClick={onClose} />

                <div className="content">
                    <div>{children}</div>

                    <div className="botoes">
                        <button className="btnSim" onClick={handleClick}>Sim</button>
                        <button onClick={onClose} className="btnNao">NÃ£o</button>
                    </div>
                </div>

            </div>

                <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
                    <Alert onClose={handleClose} severity="success" sx={{ width: '100%' }}>
                        This is a success message!
                    </Alert>
                </Snackbar>
                <Alert severity="error">This is an error message!</Alert>
                <Alert severity="warning">This is a warning message!</Alert>
                <Alert severity="info">This is an information message!</Alert>
                <Alert severity="success">This is a success message!</Alert>

        </ModalStyle>



    );

}

export default ModalConfirmacao;
