import { SideBarStyle } from './styles';
import React, { useRef, useState, useEffect } from "react";
import GroupsIcon from '@mui/icons-material/Groups';

function SideBar(props) {

    const [bg, changeBGColor] = React.useState(0);

    return (
        <SideBarStyle>
            <div className="container">
                <div className='head'>
                    <h1><span>Soccer</span>Manager</h1>
                    <div className='line'></div>
                </div>

                <div className='menu'>
                    <li>
                        <ul className='teste1'
                         onClick={() => changeBGColor(1)}
                         style= {{ backgroundColor: bg === 1 ? "#444" : "#1E212A" }}><GroupsIcon /> MyTeam</ul>


                        <ul className='teste2' 
                        onClick={() => changeBGColor(2)}
                        style= {{ backgroundColor: bg === 2 ? "#444" : "#1E212A" }}>Mercado</ul>


                        <ul className='teste3' onClick={() => changeBGColor(3)}
                        style= {{ backgroundColor: bg === 3 ? "#444" : "#1E212A" }}>Clubes</ul>


                        <ul className='teste4' onClick={() => changeBGColor(4)}
                         style= {{ backgroundColor: bg === 4 ? "#444" : "#1E212A" }}>Jogar Partida</ul>


                        <ul className='teste5' onClick={() => changeBGColor(5)}
                        style= {{ backgroundColor: bg === 5 ? "#444" : "#1E212A" }}>Sair</ul>

                    </li>
                </div>

                <div className='user'></div>


            </div>
        </SideBarStyle>

    );
}

export default SideBar;
