import styled from 'styled-components'


export const CardStyle = styled.div`

    .container {
        padding-top: 60px;
        display: flex;
        flex-direction: column;
    }

    .search {
        display: flex;
        background: red;
            & svg {
                margin-left: 85px;
                padding: 50px;
            }
    }

    input {
        margin-left 50%;
        margin-top: 0px;
        margin-bottom: 75px;

        width: 350px;
        height: 40px;
        padding-left: 10px;
    }

    .card {
        background: #1E212A;
        width: 365px;
        height: 190px;
        margin-left: 300px;
        display: flex;
        border-radius: 10px; 
        justify-content: space-between;
        cursor: pointer;
    }


    .image {
        width: 115px;
        height: 115px;
        background: black;
        margin-top: 12px;
        margin-left: 12px;
        border-radius: 10px;
    }

    .infos {
        margin-right: 115px;
        display: flex;
        flex-direction: column;
        justify-content: space-evenly;
    }

    p {
        margin: 0;
        color: #ffff;
        font-weight: 450;
    }


`
