import React, { useState } from "react";
import { MercadoStyle } from './styles'
import SideBar from "../../Components/SideBar";
import CardJogador from "../../Components/CardJogador";
import SearchBar from "../../Components/ SearchBar";
import ModalConfirmacao from "../../Components/ModalConfirmacao";
import MuiAlert, { AlertProps } from '@mui/material/Alert';
import { Snackbar, SnackbarOrigin } from "@mui/material";


export interface State extends SnackbarOrigin {
    open: boolean;
}


const Alert = React.forwardRef<HTMLDivElement, AlertProps>(function Alert(
    props,
    ref,
) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});



function Mercado() {

    const [isModalVisible, setIsModalVisible] = useState<boolean>(false);
    const [idJogador, setIdJogador] = useState<number>();


    const [state, setState] = React.useState<State>({
        open: false,
        vertical: 'bottom',
        horizontal: 'right',
    });

    const { vertical, horizontal, open } = state;


    const handleClose = () => {
        setState({ ...state, open: false });
    };


    const handleOpen = () => {
        setState({ ...state, open: true });
    };


    return (
        <>
            <MercadoStyle>

                <div className="container">
                    <SideBar selected="mercado" />
                    <SearchBar />
                    {isModalVisible ? <ModalConfirmacao onClose={() => setIsModalVisible(false)} idJogador={idJogador} setOpen={setOpen} children={<h1>Gostaria realmente de comprar esse jogador?</h1>} /> : null}

                    <CardJogador setIsModalVisible={setIsModalVisible} setIdJogador={setIdJogador} />
                </div>

                <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
                    <Alert onClose={handleClose} severity="success" sx={{ width: '100%' }}>
                        This is a success message!
                    </Alert>
                </Snackbar>

            </MercadoStyle>
        </>
    );
}

export default Mercado;
