import styled from "styled-components";

export const MyTeamStyle = styled.div`

    background-image: linear-gradient(#D3D3D3, #808080);
    overflow: hidden;


    .container {
        margin: 0;
        width: 100vw;
        height: 100vh;
        overflow-x: hidden;
        display: flex;
        justify-content: space-between;
    }

    .jogadores{
        margin-left: 25%;
        margin-top: 5%;
        background: #1E212A;
        width: 30%;
        height: 80vh;
        border-radius: 5px;
    }

    .jogador {

    }

    .campo {
        height: 75%;
        width: 32vw;
        align-itens: center;
        display: flex;
        justify-content: center;
        margin-right: 7%;
        margin-top: 6%;
    }

}

`
