import React, { useState } from "react";
import { ModalStyle } from "./styles";
import CloseIcon from '@mui/icons-material/Close';
import { apiMock } from "../../Api";
import MuiAlert from '@mui/material/Alert';


interface Jogador {
    nomeJogador: string;
    idadeJogador: number;
    nacionalidadeJogador: string;
    clubeJogador: string;
    posicaoJogador: string;
    overallJogador: number;
    valorJogador: number;
    fotoJogador: string;
    idUsuario: number;
    id: number;
}

interface Usuario {
    id: number,
    username: string,
    email: string,
    password: string
    wallet: number
}

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});



const ModalConfirmacao = ({ onClose = () => { }, children, idJogador }) => {

    let usuario = localStorage.getItem('usuario');
    const usuarioData: Usuario = JSON.parse(usuario)


    async function PatchIdUsuario() {
        try {
            const res = await apiMock.patch<Jogador[], any>(`/jogadores/${idJogador}`, {

                idUsuario: usuarioData[0].id

            });

            onClose();

        } catch (err) {
            console.log(err)
        }
    }

    return (

        <ModalStyle>
            <div className="modal">
                <CloseIcon onClick={onClose} />

                <div className="content">
                    <div>{children}</div>

                    <div className="botoes">
                        <button className="btnSim" onClick={PatchIdUsuario}>Sim</button>
                        <button onClick={onClose} className="btnNao">NÃ£o</button>
                    </div>
                </div>

            </div>
        </ModalStyle>

    );

}

export default ModalConfirmacao;
