import { SideBarStyle } from './styles';
import React, { useRef, useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import GroupsIcon from '@mui/icons-material/Groups';
import LocalAtmIcon from '@mui/icons-material/LocalAtm';
import PolicyIcon from '@mui/icons-material/Policy';
import SportsIcon from '@mui/icons-material/Sports';
import LogoutIcon from '@mui/icons-material/Logout';
import AccountBoxIcon from '@mui/icons-material/AccountBox';

interface Usuario {
    id: number,
    username: string,
    email: string,
    password: string
}

function SideBar(props) {

    const [color, setColor] = React.useState(0);
    const [username, setUsername] = useState<Usuario>();

    let usuario: Usuario = localStorage.getItem('usuario');
    usuario = JSON.parse(usuario);
    setUsername();

    return (
        <SideBarStyle>
            <div className="container">
                <div className='head'>
                    <Link to='/'><h1 id='logo'><span>Soccer</span>Manager</h1></Link>
                    <div className='line'></div>
                </div>

                <div className='menu'>
                    <ul>
                        <Link to='/myteam'>
                            <li id='myteam'
                                onClick={() => setColor(1)}
                                style={{ backgroundColor: props.selected === "myteam" ? "#444" : "#1E212A" }}>
                                <GroupsIcon />
                                MyTeam
                            </li>
                        </Link>


                        <Link to='/mercado'>
                            <li id='mercado'
                                onClick={() => setColor(2)}
                                style={{ backgroundColor: props.selected === "mercado" ? "#444" : "#1E212A" }}>
                                <LocalAtmIcon />
                                Mercado
                            </li>
                        </Link>


                        <li id='clubes' onClick={() => setColor(3)}
                            style={{ backgroundColor: props.selected === "clube" ? "#444" : "#1E212A" }}>
                            <PolicyIcon />
                            Clubes
                        </li>


                        <li id='jogar' onClick={() => setColor(4)}
                        style={{ backgroundColor: props.selected ===  "jogar" ? "#444" : "#1E212A" }}>
                            <SportsIcon />
                            Jogar Partida
                        </li>


                        <Link to='/perfil'>
                            <li id='perfil' onClick={() => setColor(5)}
                                style={{ backgroundColor: color === 5 ? "#444" : "#1E212A" }}>
                                <AccountBoxIcon />
                                Perfil
                            </li>
                        </Link>


                        <Link to='/'>
                            <li id='sair' onClick={() => setColor(6)}
                                style={{ backgroundColor: color === 6 ? "#444" : "#1E212A" }}>
                                <LogoutIcon />
                                Sair
                            </li>
                        </Link>

                    </ul>
                </div>

                <div className='userLine'></div>

                <div>

                </div>


            </div>
        </SideBarStyle>

    );
}

export default SideBar;
