import React, { useEffect, useState } from "react";
import CloseIcon from '@mui/icons-material/Close';
import { ModalStyle } from "./styles";
import { apiMock } from "../../Api";


interface Jogador {
    nomeJogador: string;
    idadeJogador: number;
    nacionalidadeJogador: string;
    clubeJogador: string;
    posicaoJogador: string;
    overallJogador: number;
    fotoJogador: string;
    id: number;
}


const Modal = ({ onClose = () => { }, children }) => {
    const [nomeJogador, setNomeJogador] = useState("");
    const [idadeJogador, setIdadeJogador] = useState("");
    const [nacionalidadeJogador, setNacionalidadeJogador] = useState("");
    const [clubeJogador, setClubeJogador] = useState("");
    const [posicaoJogador, setPosicaoJogador] = useState("");
    const [overallJogador, setOverallJogador] = useState("");
    const [fotoJogador, setFotoJogador] = useState("");

    const [jogador, setJogador] = useState<Jogador>();



    async function GetJogadorById() {
        try {
            const res = await apiMock.get<Jogador, any>(`/jogadores/${children}`);
            setJogador(res.data);
            console.log(res.data);
        } catch (err) {
            console.log(err)
        }
    }


    useEffect(() => {
        GetJogadorById();
    }, []);


    return (
        <ModalStyle>
            <div className="modal">
                <CloseIcon onClick={onClose} />


                {jogador && (

                    <div className="content">

                        <div>
                            <div className="fotoPerfil">
                                <img src="" alt="" />
                            </div>
                        </div>

                        <div className="infos">
                            <ul>
                                <li>Nome: {jogador.nomeJogador}</li>
                                <li>Idade: {jogador.idadeJogador}</li>
                                <li>Nacionalidade: {jogador.nacionalidadeJogador}</li>
                                <li>Clube: {jogador.clubeJogador}</li>
                                <li>Posição: {jogador.posicaoJogador}</li>
                                <li>Overall: {jogador.overallJogador}</li>
                            </ul>
                        </div>
                    </div>

                )}

            </div>
        </ModalStyle>
    );
};


export default Modal;
