import styled from "styled-components";

export const PerfilStyle = styled.div`

    background-image: linear-gradient(#D3D3D3, #808080);
    overflow: hidden;

    .container {
        margin: 0;
        width: 100vw;
        height: 100vh;
        overflow-x: hidden;
        display: flex;
    }

    .content {
        width: 60vw;
        height: 90vh;
        background: blue;
        margin-top: 35px;
        margin-left: 250px;
        border-radius: 10px;
        background: #1E212A;

        display: flex;
        justify-content: space-between;
    }

    .divFotoPerfil {
        width: 230px;
        height: 230px;
        background: black;
        border-radius: 115px;

        margin-left: 10%;
        margin-top: 5%;
    }

    .infos {
        display: flex;
        flex-direction: column;
        margin-top: 5%;
        margin-right: 10%;
        width: auto;

        background: green;
    }

    .inputs {
        display: flex;
        flex-direction: column;
    }

    p {
        margin: 0;
    }

    input {
        margin: 0;
        width: 300px;
        height: 30px;
    }

`
