import { useContext, useState } from "react";
import SideBar from "../../Components/SideBar";
import Context from "../../Context/context";
import { JogarStyle } from "./styles";
import x from "../../imagens/x.png"
import vs2 from "../../imagens/vs2.png"
import Jogador from "../../Interfaces/jogadorInterface";
import { api } from "../../Api";



function Jogar() {
    const { usuario, setUsuario } = useContext(Context);

    const [jogadores, setJogadores] = useState<Jogador[]>()

    async function GetAllJogadores() {
        try {
            const res = await api.get<Jogador[], any>(`/jogadores`);
            setJogadores(res.data);

        } catch (err) {
            console.log(err)
        }
    }


    return (
        <>
            <SideBar selected="jogar" />
            <JogarStyle>
                <div className="container">
                    <div className="content">

                        <div className="divSuperior">

                            <div className="myClub">
                                <h2>{usuario?.nomeClube}</h2>
                                <img className="fotoUsuario" src={usuario?.fotoPerfil} alt="" />
                                <h2>{usuario?.username}</h2>
                            </div>

                            <div className="vs">
                                <img src={vs2} alt="" />
                            </div>


                            <div className="adversrio">
                                <h2>{usuario?.nomeClube}</h2>
                                <img className="fotoUsuario" src={usuario?.fotoPerfil} alt="" />
                                <h2>{usuario?.username}</h2>
                            <button className="">Sortear adversario</button>

                            </div>

                        </div>

                        <div className="divInferior">
                            <div className="aposta">
                                <p>Aposta</p>
                                <input type="number" placeholder="Insira um valor de aposta" name="" id="" />
                                <button>Jogar</button>
                            </div>
                        </div>

                    </div>
                </div>
            </JogarStyle>

        </>
    );
}

export default Jogar;
