import styled from "styled-components";

export const JogarStyle = styled.div`

    background-image: linear-gradient(#D3D3D3, #808080);
    overflow: hidden;

    .container {
        margin: 0;
        width: 95vw;
        margin-left: auto;
        height: 100vh;
        overflow-x: hidden;
        display: flex;
    }

    .content {
        width: 76vw;
        height: 87vh;
        background: #DCDCDC;
        margin-top: 45px;
        margin-left: 14%;

        border-radius: 4px;

