import React, { useEffect, useState } from "react";
import { api } from "../../Api";
import SideBar from "../../Components/SideBar";
import { PerfilStyle } from './styles'


interface Usuario {
    id: number,
    username: string,
    email: string,
    password: string,
    wallet: number,
    fotoPerfil: String
}

function Perfil() {
    // onChange={e => setName(e.target.value)}

    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [wallet, setWallet] = useState<number>()
    const [fotoPerfil, setFotoPerfil] = useState("")




    useEffect(() => {
        let usuario = localStorage.getItem('usuario');
        const usuarioData: Usuario = JSON.parse(usuario)
        setUsername(usuarioData.username)
    }, []);

    // async function GetUsuarioById() {
    //     try {
    //         const res = await api.get<Usuario>(`/usuarios/${usuarioData.id}`);

    //     } catch (err) {
    //         console.log(err)
    //     }
    // }


    async function editarPerfil() {
        try {
            const { data } = await api.put("/usuarios/update", {

                id: usuarioData.id,
                username: username,
                email: email,
                password: password,
                wallet: wallet,
                fotoPerfil: fotoPerfil
            });

        } catch (error) {
            console.log(error);
        }
    }



    return (
        <>
            <SideBar selected="perfil" />
            <PerfilStyle>
                <div className="container">

                    <div className="content">

                        <div className="foto-adicionar">
                            <div className="divFotoPerfil">
                                <img src="" alt="" />
                            </div>
                            <p>Adicionar foto</p>
                        </div>


                        <div className="infos">
                            <div className="inputs">

                                <p>Username:</p>
                                <input type="Username"
                                    value={username}
                                    onChange={e => setUsername(e.target.value)}
                                />


                                <p>E-mail:</p>
                                <input type="text" />


                                <p>Wallet:</p>
                                <input type="text" />
                            </div>

                            <button>Salvar Alterações</button>
                        </div>

                    </div>
                </div>
            </PerfilStyle>
        </>
    );
}

export default Perfil;
