import styled from "styled-components";

export const PerfilStyle = styled.div`

    background-image: linear-gradient(#D3D3D3, #808080);
    overflow: hidden;

    .container {
        margin: 0;
        width: 100vw;
        height: 100vh;
        overflow-x: hidden;
        display: flex;
    }

    .content {
        width: 77vw;
        height: 90vh;
        background: blue;
        margin-top: 35px;
        margin-left: 250px;
        border-radius: 10px;
        background: #1E212A;

        display: flex;
        justify-content: space-between;
    }

`
