import React, { useContext, useEffect, useState } from "react";
import { SearchBarStyle } from './styles';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import Modal from "../ModalCadastroJogador.tsx";
import Context from "../../Context/context";
import Jogador from "../../Interfaces/jogadorInterface";


function SearchBar() {
    const { jogadores, setJogadores } = useContext(Context);
    const [jogadoresFiltrados, setJogadoresFiltrados] = useState<Jogador[]>([]);
    const [achou, setAchou] = useState<boolean>(false);

    const [searchTerm, setSearchTerm] = useState("");

    const [isModalVisible, setIsModalVisible] = useState<boolean>(false);


    function filtrar() {
        try {

            jogadores.filter((jogador) => {
                if (searchTerm.length === 0) {
                    return jogadores

                } else if (jogador.nomeJogador.toLowerCase().includes(searchTerm.toLowerCase())) {
                    // setJogadoresFiltrados(oldArray => ([...oldArray, jogador]));
                    setJogadoresFiltrados(jogador)
                    console.log(jogadoresFiltrados);

                    if (jogadoresFiltrados.length > 0) {
                        setJogadores([])
                        console.log("achou")
                        setAchou(true)

                        if(achou){
                            setJogadores(jogador)
                            console.log("achou msm")
                        }
                    }

                }
            })

        } catch (err) {
            console.error(err);
        }

    }

    useEffect(() => {
        filtrar();
    }, [searchTerm])


    return (
        <SearchBarStyle>
            <div className="search">
                {isModalVisible ? <Modal onClose={() => setIsModalVisible(false)} children={<h1>teste</h1>} /> : null}

                <input type="text"
                    value={searchTerm}
                    placeholder="Pesquisar Jogador"
                    onChange={(e) => { setSearchTerm(e.target.value) }}
                />

                <AddCircleIcon onClick={() => setIsModalVisible(true)} />
            </div>
        </SearchBarStyle>
    );
}

export default SearchBar;
