import React, { useState } from "react";
import CloseIcon from '@mui/icons-material/Close';
import { ModalStyle } from "./styles";
import { apiMock } from "../../Api";

const Modal = ({ onClose = () => { }, children }) => {
    const [nomeJogador, setNomeJogador] = useState("");
    const [idadeJogador, setIdadeJogador] = useState("");
    const [nacionalidadeJogador, setNacionalidadeJogador] = useState("");
    const [clubeJogador, setClubeJogador] = useState("");
    const [posicaoJogador, setPosicaoJogador] = useState("");
    const [overallJogador, setOverallJogador] = useState("");
    const [fotoJogador, setFotoJogador] = useState("");



    async function CadastrarJogador(): Promise<void> {
        try {
            const res = await apiMock.post("/jogadores", {

                username: username,
                email: email,
                password: password

            });

            changePage();
            setErro(false);

        } catch (err) {
            console.log(err)
            setErro(true);
        }
    }





    return (
        <ModalStyle>
            <div className="modal">
                <CloseIcon onClick={onClose} />

                <div className="content">

                    <div>
                        <div className="fotoPerfil">
                            <img src="" alt="" />
                        </div>
                        <p>Adicionar Imagem</p>
                    </div>


                    <div className="infos">
                        <div>
                            <h1>Novo Jogador</h1>
                        </div>
                        <input type="text" placeholder="Nome" />
                        <input type="text" placeholder="Idade" />
                        <input type="text" placeholder="Nacionalidade" />
                        <input type="text" placeholder="Clube" />
                        <input type="text" placeholder="Posição" />
                        <input type="text" placeholder="Overall" />

                        <button className="btn">Cadastrar</button>
                    </div>

                </div>
            </div>
        </ModalStyle>
    );
};

export default Modal;
