import { SideBarStyle } from './styles';
import React, { useRef, useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import GroupsIcon from '@mui/icons-material/Groups';
import LocalAtmIcon from '@mui/icons-material/LocalAtm';
import PolicyIcon from '@mui/icons-material/Policy';
import SportsIcon from '@mui/icons-material/Sports';
import LogoutIcon from '@mui/icons-material/Logout';
import Mercado from '../../Pages/Mercado';

function SideBar(props) {

    const [bg, changeBGColor] = React.useState(0);

    return (
        <SideBarStyle>
            <div className="container">
                <div className='head'>
                    <h1 id='logo'><span>Soccer</span>Manager</h1>
                    <div className='line'></div>
                </div>

                <div className='menu'>
                    <ul>
                        <li id='myteam'
                            onClick={() => changeBGColor(1)}
                            style= {{ backgroundColor: bg === 1 ? "#444" : "#1E212A" }}>
                            <GroupsIcon />
                            MyTeam
                        </li>


                       <Link to="/mercado" element={<Mercado />}> <li id='mercado'
                            onClick={() => changeBGColor(2)}
                            style= {{ backgroundColor: bg === 2 ? "#444" : "#1E212A" }}>
                            <LocalAtmIcon />
                            Mercado
                        </li> </Link>


                        <li id='clubes' onClick={() => changeBGColor(3)}
                            style= {{ backgroundColor: bg === 3 ? "#444" : "#1E212A" }}>
                            <PolicyIcon />    
                            Clubes
                        </li>


                        <li id='jogar' onClick={() => changeBGColor(4)}
                            style= {{ backgroundColor: bg === 4 ? "#444" : "#1E212A" }}>
                            <SportsIcon />    
                            Jogar Partida
                        </li>


                        <li id='sair' onClick={() => changeBGColor(5)}
                            style= {{ backgroundColor: bg === 5 ? "#444" : "#1E212A" }}>
                            <LogoutIcon />    
                            Sair
                        </li>

                    </ul>
                </div>

                <div className='user'></div>


            </div>
        </SideBarStyle>

    );
}

export default SideBar;
