import React, { useEffect, useState } from "react";
import { CardStyle } from './styles'
import AddCircleIcon from '@mui/icons-material/AddCircle';
import api from "../../Api";

interface Jogador {

    nome: string;
    idade: number;
    nacionalidade: string;
    clube: string;
    posicao: string;
    overall: number;
    foto: string;
    id: number;
}

function CardJogador() {

    const [isModalVisible, setIsModalVisible] = useState<boolean>(false);
    const [jogadores, setJogadores] = useState<Jogador[]>();

    async function GetJogadores() {
        try {
            const res = await api.get<Jogador[], any>("/jogadores");
            setJogadores(res.data);
            console.log(jogadores);
        } catch (err) {
            console.log(err)
        }
    }

    useEffect(() => {
        GetJogadores();
    }, []);


    return (
        <CardStyle>

            <div className="container">

                <div className="search">
                    <input type="text" placeholder="Pesquisar Jogador" />
                   <div onClick={setIsModalVisible(true)}><AddCircleIcon /></div> 
                </div>






                <div className="cards">

                    {jogadores && jogadores.map((jogador) => (

                        <div className="card">

                            <div className="image"><img src={jogador.foto} alt="" /></div>

                            <div className="infos">
                                <p id="nome">Nome: {jogador.nome}</p>
                                <p id="idade">Idade: {jogador.idade}</p>
                                <p id="nascionalidade">Nacionalidade: {jogador.nacionalidade}</p>
                                <p id="clube">Clube: {jogador.clube}</p>
                                <p id="posicao">Posição: {jogador.posicao}</p>
                                <p id="over">Overall: {jogador.overall}</p>
                            </div>

                        </div>

                    ))}

                </div>

            </div>


        </CardStyle >

    );
}

export default CardJogador;
