import styled from 'styled-components'

export const HeaderStyle = styled.div`
    .header {
        width: 100%;
        height: 100px;
        background: #1E212A;
        display: flex;
        justify-content: space-between;
        flex-direction: row;
        align-items: center;
    }

    h1 {
        margin: 0;
        color: #ffff;
        font-size: 40px;
    }

    span {
        color: #00FF7F;
    }


    .menu {
        background-color: white;
        margin-right: 10%;
        width: auto;
        display: flex;
        flex-direction: row;
        justify-content: space-around;   
    }
`
