import React, { useEffect, useState } from "react";
import { CardStyle } from './styles'
import api from "../../Api";



interface Jogador {
    nome: string;
    idade: number;
    nacionalidade: string;
    clube: string;
    posicao: string;
    overall: number;
    foto: string;
    id: number;
}


function CardJogador() {

    const [jogadores, setJogadores] = useState<Jogador[]>();

    async function GetJogadores() {
        try {
            const res = await api.get<Jogador[], any>("/jogadores");
            setJogadores(res.data);
            console.log(res.data);
        } catch (err) {
            console.log(err)
        }
    }


    useEffect(() => {
        GetJogadores();
    }, []);


    return (
        <CardStyle>

            <div className="container">

                <div className="cards">

                    {jogadores && jogadores.map((jogador) => (

                        <div className="card">

                            <div className="image"><img src={jogador.foto} alt="" /></div>

                            <div className="infos">
                                <p id="nome">Nome: <span>{jogador.nome}</span></p>
                                <p id="idade">Idade: <span>{jogador.idade}</span></p>
                                <p id="nascionalidade">Nacionalidade: <span>{jogador.nacionalidade}</span></p>
                                <p id="clube">Clube: <span>{jogador.clube}</span></p>
                                <p id="posicao">Posição: <span>{jogador.posicao}</span></p>
                                <p id="over">Overall: <span>{jogador.overall}</span></p>
                            </div>

                        </div>

                    ))}

                </div>

            </div>


        </CardStyle >

    );
}

export default CardJogador;
