                </div>

                <div className='menu'>
                    <li>
                        <ul className='teste1'
                         onClick={() => changeBGColor(1)}
                         style= {{ backgroundColor: bg === 1 ? "black" : "red" }}>MyTeam</ul>



                        <ul className='teste2'>Mercado</ul>
                        <ul className='teste3'>Clubes</ul>
                        <ul className='teste4'>Jogar Partida</ul>
                        <ul className='teste5'>Sair</ul>

                    </li>
                </div>

                <div className='user'></div>


            </div>
        </SideBarStyle>

    );
}

export default SideBar;
