import api from '../../Api';


export interface LoginProps{
    setPage: Dispatch<SetStateAction<string | undefined>>;
}


const Login:React.FC<LoginProps> = ({setPage}) => {
    const [name, setName] = useState("");
    const [password, setPassword] = useState("");

    function changePage(){
        setPage('cadastrar');
    }

