    return (
        <LoginStyle>
            <div className="menu">
                <Link to="/"> <h1>Soccer<span>Manager</span></h1> </Link>

                <h2>Entrar</h2>

                <div className="form">

                    <input type="text" placeholder="Username" onChange={e => setName(e.target.value)}/>
