import styled from "styled-components";

export const MyTeamStyle = styled.div`

    background-image: linear-gradient(#D3D3D3, #808080);
    overflow: hidden;

    .container {
        margin: 0;
        width: 100vw;
        height: 100vh;
        overflow-x: hidden;
        display: flex;
        justify-content: space-between;
    }

    .jogadores{
        margin-left: 25%;
        margin-top: 5%;
        background: #1E212A;
        width: 30%;
        height: 80vh;
        border-radius: 5px;
        display: flex;
        flex-direction: column;
        justify-content: center;

    }

    .jogador {
        padding-top: 15px;
        width: 350px;
        height: 80px;
        background: #C0C0C0;
        border-radius: 5px;
        display: flex;
        justify-content: space-between;

    }

    .foto {
        width: 65px;
        height: 65px;
        background: black;
        margin-left: 20px;
    }

    ul {
        margin: 0;
        align-itens: center;
    }


    li {
        list-style: none;
        color: #222;
        margin-right: 100px;
        padding: 0px;
    }

    .campo {
        height: 75%;
        width: 32vw;
        align-itens: center;
        display: flex;
        justify-content: center;
        margin-right: 7%;
        margin-top: 6%;
    }

}

`
