import { parse } from "path";
import React, { useEffect, useState } from "react";
import { api } from "../../Api";
import SideBar from "../../Components/SideBar";
import { PerfilStyle } from './styles'


interface Usuario {
    id: number,
    username: string,
    email: string,
    password: string,
    wallet: number,
    fotoPerfil: String
}

function Perfil() {
    const [usuarioData, setUsuarioData] = useState<Usuario>()
    const [userData, setUserData] = useState<Usuario>()


    let user = localStorage.getItem('usuario');
    setUserData(JSON.parse(user))



    async function localStorageUpdate() {
        try {
            const res = await api.get(`/usuarios/${userData.id}`);
            localStorage.setItem('usuario', JSON.stringify(res.data))

        } catch (error) {
            console.log(error);
        }
    }


    async function editarPerfil() {
        try {
            const { data } = await api.put("/usuarios/update", {

                id: usuarioData.id,
                username: usuarioData.username,
                email: usuarioData.email,
                password: usuarioData.password,
                wallet: usuarioData.wallet,
                fotoPerfil: usuarioData.fotoPerfil
            });



        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        localStorageUpdate();
        let usuario = localStorage.getItem('usuario');
        setUsuarioData(JSON.parse(usuario))
    }, []);


    return (
        <>
            <SideBar selected="perfil" />
            <PerfilStyle>
                <div className="container">

                    <div className="content">

                        <div className="foto-adicionar">
                            <div className="divFotoPerfil">
                                <img src="" alt="" />
                            </div>
                            <p>Adicionar foto</p>
                        </div>


                        <div className="infos">
                            <div className="inputs">

                                <p>Username:</p>
                                <input type="text"
                                    value={usuarioData?.username}
                                    onChange={e => setUsuarioData(prevState => ({ ...prevState, username: e.target.value }))}
                                />

                                <p>E-mail:</p>
                                <input type="text"
                                    value={usuarioData?.email}
                                    onChange={e => setUsuarioData(prevState => ({ ...prevState, email: e.target.value }))}
                                />

                                <p>Wallet:</p>
                                <input type="number"
                                    value={usuarioData?.wallet}
                                    onChange={e => setUsuarioData(prevState => ({ ...prevState, wallet: parseInt(e.target.value) }))}
                                />


                                <p>Password:</p>
                                <input type="password"
                                    value={usuarioData?.password}
                                    onChange={e => setUsuarioData(prevState => ({ ...prevState, password: e.target.value }))}
                                />
                            </div>

                            <button onClick={() => editarPerfil()}>Salvar Alterações</button>
                        </div>

                    </div>
                </div>
            </PerfilStyle>
        </>
    );
}

export default Perfil;
