        flex-direction: column;
        z-index: 100;
    }

    & svg {
        margin-left: 100px;
        margin-top: 5px;
        align-self: end;
    }


    .content {
        widht: 100vw;
        height: 82vh;
        display: flex;
        justify-content: space-between;
        margin: 0px;
    }

