import { SideBarStyle } from './styles';
import React, { useRef, useState, useEffect } from "react";
import GroupsIcon from '@mui/icons-material/Groups';
import LocalAtmIcon from '@mui/icons-material/LocalAtm';

function SideBar(props) {

    const [bg, changeBGColor] = React.useState(0);

    return (
        <SideBarStyle>
            <div className="container">
                <div className='head'>
                    <h1><span>Soccer</span>Manager</h1>
                    <div className='line'></div>
                </div>

                <div className='menu'>
                    <ul>
                        <li id='myteam'
                            onClick={() => changeBGColor(1)}
                            style= {{ backgroundColor: bg === 1 ? "#444" : "#1E212A" }}>
                            <GroupsIcon />
                            MyTeam
                        </li>


                        <li id='mercado'
                            onClick={() => changeBGColor(2)}
                            style= {{ backgroundColor: bg === 2 ? "#444" : "#1E212A" }}>
                            Mercado
                        </li>


                        <li id='clubes' onClick={() => changeBGColor(3)}
                            style= {{ backgroundColor: bg === 3 ? "#444" : "#1E212A" }}>
                            Clubes
                        </li>


                        <li id='jogar' onClick={() => changeBGColor(4)}
                            style= {{ backgroundColor: bg === 4 ? "#444" : "#1E212A" }}>
                            Jogar Partida
                        </li>


                        <li id='sair' onClick={() => changeBGColor(5)}
                            style= {{ backgroundColor: bg === 5 ? "#444" : "#1E212A" }}>
                            Sair
                        </li>

                    </ul>
                </div>

                <div className='user'></div>


            </div>
        </SideBarStyle>

    );
}

export default SideBar;
